<?php require 'utility.php';require 'serverlogin.php';session_start();$username = check($_POST['username']);$password = check($_POST['password']);$phone = check($_POST['phone']);$address = check($_POST['address']);$email = check($_POST['email']);$fname = check($_POST['fname']);$mname = check($_POST['mname']);$lname = check($_POST['lastname']);if($username == "NULL") {  $_SESSION['errormessage'] = "Please enter a username";  header("Location: createaccount.php");  exit();}if($password == "NULL") {  $_SESSION['errormessage'] = "Please enter a password";  header("Location: createaccount.php");  exit();}//check to see if there is already a user with that username$sql = "SELECT U.Username FROM user U WHERE U.Username = \"" . $username . "\";";$result = $conn->query($sql);if($result == FALSE) {  $_SESSION['errormessage'] = "There was an error";  header("Location: createaccount.php");  exit();}if($result->num_rows != 0) {  $_SESSION['errormessage'] = "There is already a user with that username";  header("Location: createaccount.php");  exit();}//insertion queries$sql = "INSERT INTO USER (username, password, phone, address, email, fname, mname, lname) " . values(array($username, $password, $phone, $address, $email, $fname, $mname, $lname));$result = $conn->query($sql);$sql = "INSERT INTO BUYER (username) " . values(array($username));$result = $conn->query($sql);if($result == TRUE) {  $_SESSION['errormessage'] = "Account Created";  header("Location: login.php");  exit();}else {  $_SESSION['errormessage'] = "There was an error creating the account";  header("Location: createaccount.php");  exit();}?>